    def find_best_response(self, user_message: str) -> str:
        """ĞĞ°Ñ…Ğ¾Ğ´Ğ¸Ğ¼ Ñ‚Ğ¾Ñ‡Ğ½Ñ‹Ğ¹ Ğ¾Ñ‚Ğ²ĞµÑ‚ Ğ½Ğ° Ğ²Ğ¾Ğ¿Ñ€Ğ¾Ñ"""
        user_lower = user_message.lower().strip()
        
        # Ğ£Ğ´Ğ°Ğ»ÑĞµĞ¼ Ğ·Ğ½Ğ°ĞºĞ¸ Ğ¿Ñ€ĞµĞ¿Ğ¸Ğ½Ğ°Ğ½Ğ¸Ñ Ğ´Ğ»Ñ Ğ»ÑƒÑ‡ÑˆĞµĞ³Ğ¾ ÑĞ¾Ğ¿Ğ¾ÑÑ‚Ğ°Ğ²Ğ»ĞµĞ½Ğ¸Ñ
        user_clean = user_lower.replace('?', '').replace('!', '').replace('.', '')
        
        # Ğ˜Ñ‰ĞµĞ¼ Ñ‚Ğ¾Ñ‡Ğ½Ğ¾Ğµ ÑĞ¾Ğ²Ğ¿Ğ°Ğ´ĞµĞ½Ğ¸Ğµ
        if user_clean in self.qa_pairs:
            return self.qa_pairs[user_clean]
        
