        for question, answer in self.qa_pairs.items():
            question_clean = question.replace('?', '').replace('!', '').replace('.', '')
            if question_clean in user_clean or user_clean in question_clean:
                return answer
        
        # Ğ•ÑĞ»Ğ¸ Ğ½Ğµ Ğ½Ğ°ÑˆĞ»Ğ¸, Ğ¸ÑĞ¿Ğ¾Ğ»ÑŒĞ·ÑƒĞµĞ¼ Ğ¾Ğ±Ñ‰Ğ¸Ğµ Ğ¾Ñ‚Ğ²ĞµÑ‚Ñ‹
